import React, { useState, useRef } from 'react';
import { X, RotateCw, Home, Settings, AlertTriangle } from 'lucide-react';

// Initial empty quiz data structure
const createEmptyQuiz = () => {
  const quiz = [];
  for (let row = 0; row < 5; row++) {
    for (let col = 0; col < 6; col++) {
      quiz.push({
        id: `block-${col}-${row}`,
        col,
        row,
        question: { content: [] },
        answer: { content: [] },
        isRead: false
      });
    }
  }
  return quiz;
};

const QuizApp = () => {
  // Screen management
  const [currentScreen, setCurrentScreen] = useState('home'); // 'home', 'main', 'admin'

  // Quiz data
  const [quizData, setQuizData] = useState(createEmptyQuiz());
  const [columnNames, setColumnNames] = useState(['', '', '', '', '', '']);

  // Modal state
  const [openBlock, setOpenBlock] = useState(null);
  const [showAnswer, setShowAnswer] = useState(false);

  // Admin editing state
  const [editingContent, setEditingContent] = useState(null);
  const [showResetWarning, setShowResetWarning] = useState(false);

  // Handlers
  const handleBlockClick = (blockId) => {
    const block = quizData.find(b => b.id === blockId);
    setOpenBlock(block);
    setShowAnswer(false);
  };

  const handleCloseBlock = () => {
    setOpenBlock(null);
    setShowAnswer(false);
    setEditingContent(null);
  };

  const handleToggleRead = () => {
    if (!openBlock) return;
    setQuizData(prev => prev.map(block => 
      block.id === openBlock.id 
        ? { ...block, isRead: !block.isRead }
        : block
    ));
    setOpenBlock(prev => ({ ...prev, isRead: !prev.isRead }));
  };

  const handleFlip = () => {
    setShowAnswer(prev => !prev);
  };

  const handleResetBlocks = () => {
    setQuizData(prev => prev.map(block => ({ ...block, isRead: false })));
  };

  const handleResetQuestions = () => {
    setQuizData(createEmptyQuiz());
    setColumnNames(['', '', '', '', '', '']);
    setShowResetWarning(false);
    setOpenBlock(null);
  };

  const handleColumnNameChange = (colIndex, newName) => {
    setColumnNames(prev => {
      const updated = [...prev];
      updated[colIndex] = newName;
      return updated;
    });
  };

  const handleContentEdit = (type, content) => {
    if (!openBlock) return;

    setQuizData(prev => prev.map(block => 
      block.id === openBlock.id
        ? {
            ...block,
            [type]: { content }
          }
        : block
    ));

    setOpenBlock(prev => ({
      ...prev,
      [type]: { content }
    }));
  };

  const addContentItem = (type, itemType) => {
    const currentContent = openBlock[showAnswer ? 'answer' : 'question'].content || [];
    const newItem = itemType === 'text' 
      ? { type: 'text', value: '', style: {} }
      : { type: itemType, url: '', size: 100 };

    handleContentEdit(showAnswer ? 'answer' : 'question', [...currentContent, newItem]);
  };

  const updateContentItem = (index, updates) => {
    const contentType = showAnswer ? 'answer' : 'question';
    const currentContent = [...openBlock[contentType].content];
    currentContent[index] = { ...currentContent[index], ...updates };
    handleContentEdit(contentType, currentContent);
  };

  const deleteContentItem = (index) => {
    const contentType = showAnswer ? 'answer' : 'question';
    const currentContent = openBlock[contentType].content.filter((_, i) => i !== index);
    handleContentEdit(contentType, currentContent);
  };

  const moveContentItem = (index, direction) => {
    const contentType = showAnswer ? 'answer' : 'question';
    const currentContent = [...openBlock[contentType].content];
    const newIndex = direction === 'up' ? index - 1 : index + 1;

    if (newIndex >= 0 && newIndex < currentContent.length) {
      [currentContent[index], currentContent[newIndex]] = [currentContent[newIndex], currentContent[index]];
      handleContentEdit(contentType, currentContent);
    }
  };

  // Components
  const HomeScreen = () => (
    <div id="home-screen" className="min-h-screen bg-gradient-to-br from-indigo-500 to-purple-600 flex items-center justify-center">
      <div className="text-center">
        <h1 id="home-title" className="text-6xl font-bold text-white mb-12">Quiz Master</h1>
        <div className="space-y-4">
          <button
            id="btn-goto-main"
            onClick={() => setCurrentScreen('main')}
            className="block w-64 mx-auto bg-white text-indigo-600 px-8 py-4 rounded-lg text-xl font-semibold hover:bg-indigo-50 transition-all transform hover:scale-105"
          >
            Start Quiz
          </button>
          <button
            id="btn-goto-admin"
            onClick={() => setCurrentScreen('admin')}
            className="flex items-center justify-center w-64 mx-auto bg-indigo-700 text-white px-8 py-4 rounded-lg text-xl font-semibold hover:bg-indigo-800 transition-all"
          >
            <Settings className="mr-2" size={24} />
            Admin Panel
          </button>
        </div>
      </div>
    </div>
  );

  const QuizGrid = ({ isAdmin }) => (
    <div id={isAdmin ? "admin-screen" : "main-screen"} className="min-h-screen bg-gray-900 p-8">
      <div className="max-w-7xl mx-auto">
        <h1 id={isAdmin ? "admin-title" : "main-title"} className="text-4xl font-bold text-white mb-8 text-center">
          {isAdmin ? 'Admin Panel' : 'Quiz Board'}
        </h1>

        {/* Grid */}
        <div id="quiz-grid" className="grid grid-cols-6 gap-4 mb-8">
          {[0, 1, 2, 3, 4, 5].map(col => (
            <div key={col} className="space-y-4">
              {/* Column Header */}
              <input
                id={`column-header-${col}`}
                type="text"
                value={columnNames[col]}
                onChange={(e) => handleColumnNameChange(col, e.target.value)}
                placeholder={`Column ${col + 1}`}
                className="w-full bg-indigo-600 text-white text-center py-3 px-2 rounded-lg font-semibold text-lg focus:outline-none focus:ring-2 focus:ring-indigo-400"
              />

              {/* Blocks */}
              {[0, 1, 2, 3, 4].map(row => {
                const block = quizData.find(b => b.col === col && b.row === row);
                const hasContent = block?.question?.content?.length > 0;

                return (
                  <button
                    key={block.id}
                    id={block.id}
                    onClick={() => handleBlockClick(block.id)}
                    className={`w-full aspect-square rounded-lg font-bold text-2xl transition-all transform hover:scale-105 ${
                      block.isRead && !isAdmin
                        ? 'bg-gray-600 text-gray-400 opacity-50'
                        : hasContent
                        ? 'bg-gradient-to-br from-yellow-400 to-orange-500 text-white shadow-lg'
                        : 'bg-gradient-to-br from-blue-500 to-purple-600 text-white shadow-lg'
                    }`}
                  >
                    {row + 1}
                  </button>
                );
              })}
            </div>
          ))}
        </div>

        {/* Bottom Controls */}
        <div id="bottom-controls" className="flex justify-end space-x-4">
          <button
            id={isAdmin ? "btn-reset-questions" : "btn-reset-blocks"}
            onClick={() => isAdmin ? setShowResetWarning(true) : handleResetBlocks()}
            className="bg-red-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-red-700 transition-all"
          >
            {isAdmin ? 'Reset Questions' : 'Reset Blocks'}
          </button>
          <button
            id="btn-home"
            onClick={() => setCurrentScreen('home')}
            className="flex items-center bg-gray-700 text-white px-6 py-3 rounded-lg font-semibold hover:bg-gray-600 transition-all"
          >
            <Home className="mr-2" size={20} />
            Home
          </button>
        </div>
      </div>

      {/* Block Modal */}
      {openBlock && (
        <BlockModal isAdmin={isAdmin} />
      )}

      {/* Reset Warning Modal */}
      {showResetWarning && (
        <div id="reset-warning-modal" className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-8 max-w-md">
            <div className="flex items-center text-red-600 mb-4">
              <AlertTriangle size={32} className="mr-3" />
              <h2 className="text-2xl font-bold">Warning</h2>
            </div>
            <p className="text-gray-700 mb-6">
              This will delete all questions and answers. This action cannot be undone. Are you sure?
            </p>
            <div className="flex justify-end space-x-4">
              <button
                id="btn-cancel-reset"
                onClick={() => setShowResetWarning(false)}
                className="px-6 py-2 bg-gray-300 text-gray-700 rounded-lg font-semibold hover:bg-gray-400"
              >
                Cancel
              </button>
              <button
                id="btn-confirm-reset"
                onClick={handleResetQuestions}
                className="px-6 py-2 bg-red-600 text-white rounded-lg font-semibold hover:bg-red-700"
              >
                Reset All
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );

  const BlockModal = ({ isAdmin }) => {
    const contentType = showAnswer ? 'answer' : 'question';
    const content = openBlock[contentType]?.content || [];
    const fileInputRef = useRef(null);

    const handleFileUpload = (e, index) => {
      const file = e.target.files?.[0];
      if (!file) return;

      const reader = new FileReader();
      reader.onload = (event) => {
        const type = file.type.startsWith('image/') ? 'image' 
                   : file.type.startsWith('video/') ? 'video'
                   : file.type.startsWith('audio/') ? 'audio'
                   : null;

        if (type) {
          updateContentItem(index, { url: event.target.result, type });
        }
      };
      reader.readAsDataURL(file);
    };

    return (
      <div id="block-modal" className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50 p-8">
        <div className={`bg-white rounded-lg shadow-2xl w-full max-w-4xl max-h-[90vh] flex flex-col transition-all duration-500 ${showAnswer ? 'animate-flip' : ''}`}>
          {/* Header */}
          <div id="modal-header" className="flex justify-between items-center p-6 border-b">
            <h2 className="text-2xl font-bold text-gray-800">
              {showAnswer ? 'Answer' : 'Question'} - Block {openBlock.row + 1}
            </h2>
            <div className="flex space-x-2">
              {!isAdmin && (
                <button
                  id="btn-toggle-read"
                  onClick={handleToggleRead}
                  className={`px-4 py-2 rounded-lg font-semibold transition-all ${
                    openBlock.isRead
                      ? 'bg-green-600 text-white hover:bg-green-700'
                      : 'bg-gray-300 text-gray-700 hover:bg-gray-400'
                  }`}
                >
                  {openBlock.isRead ? 'Mark Unread' : 'Mark Read'}
                </button>
              )}
              <button
                id="btn-flip"
                onClick={handleFlip}
                className="flex items-center px-4 py-2 bg-indigo-600 text-white rounded-lg font-semibold hover:bg-indigo-700 transition-all"
              >
                <RotateCw className="mr-2" size={20} />
                Flip
              </button>
              <button
                id="btn-close"
                onClick={handleCloseBlock}
                className="p-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-all"
              >
                <X size={24} />
              </button>
            </div>
          </div>

          {/* Content Area */}
          <div id="modal-content" className="flex-1 overflow-y-auto p-6">
            {isAdmin ? (
              <div id="admin-editor" className="space-y-4">
                {/* Toolbar */}
                <div id="editor-toolbar" className="flex flex-wrap gap-2 p-4 bg-gray-100 rounded-lg">
                  <button
                    onClick={() => addContentItem(contentType, 'text')}
                    className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
                  >
                    + Text
                  </button>
                  <button
                    onClick={() => addContentItem(contentType, 'image')}
                    className="px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
                  >
                    + Image
                  </button>
                  <button
                    onClick={() => addContentItem(contentType, 'video')}
                    className="px-4 py-2 bg-purple-500 text-white rounded hover:bg-purple-600"
                  >
                    + Video
                  </button>
                  <button
                    onClick={() => addContentItem(contentType, 'audio')}
                    className="px-4 py-2 bg-orange-500 text-white rounded hover:bg-orange-600"
                  >
                    + Audio
                  </button>
                </div>

                {/* Content Items */}
                {content.map((item, index) => (
                  <div key={index} id={`content-item-${index}`} className="border rounded-lg p-4 bg-gray-50">
                    <div className="flex justify-between items-center mb-2">
                      <span className="font-semibold text-gray-700 capitalize">{item.type}</span>
                      <div className="flex space-x-2">
                        <button
                          onClick={() => moveContentItem(index, 'up')}
                          disabled={index === 0}
                          className="px-2 py-1 bg-gray-300 rounded hover:bg-gray-400 disabled:opacity-50"
                        >

                        </button>
                        <button
                          onClick={() => moveContentItem(index, 'down')}
                          disabled={index === content.length - 1}
                          className="px-2 py-1 bg-gray-300 rounded hover:bg-gray-400 disabled:opacity-50"
                        >

                        </button>
                        <button
                          onClick={() => deleteContentItem(index)}
                          className="px-2 py-1 bg-red-500 text-white rounded hover:bg-red-600"
                        >
                          Delete
                        </button>
                      </div>
                    </div>

                    {item.type === 'text' ? (
                      <div className="space-y-2">
                        <div className="flex space-x-2">
                          <button
                            onClick={() => updateContentItem(index, { 
                              style: { ...item.style, fontWeight: item.style?.fontWeight === 'bold' ? 'normal' : 'bold' }
                            })}
                            className={`px-3 py-1 rounded ${item.style?.fontWeight === 'bold' ? 'bg-gray-700 text-white' : 'bg-gray-200'}`}
                          >
                            <strong>B</strong>
                          </button>
                          <button
                            onClick={() => updateContentItem(index, { 
                              style: { ...item.style, fontStyle: item.style?.fontStyle === 'italic' ? 'normal' : 'italic' }
                            })}
                            className={`px-3 py-1 rounded ${item.style?.fontStyle === 'italic' ? 'bg-gray-700 text-white' : 'bg-gray-200'}`}
                          >
                            <em>I</em>
                          </button>
                          <button
                            onClick={() => updateContentItem(index, { 
                              style: { ...item.style, textDecoration: item.style?.textDecoration === 'underline' ? 'none' : 'underline' }
                            })}
                            className={`px-3 py-1 rounded ${item.style?.textDecoration === 'underline' ? 'bg-gray-700 text-white' : 'bg-gray-200'}`}
                          >
                            <u>U</u>
                          </button>
                          <button
                            onClick={() => updateContentItem(index, { 
                              style: { ...item.style, textDecoration: item.style?.textDecoration === 'line-through' ? 'none' : 'line-through' }
                            })}
                            className={`px-3 py-1 rounded ${item.style?.textDecoration === 'line-through' ? 'bg-gray-700 text-white' : 'bg-gray-200'}`}
                          >
                            <s>S</s>
                          </button>
                          <select
                            value={item.style?.fontSize || '16'}
                            onChange={(e) => updateContentItem(index, { 
                              style: { ...item.style, fontSize: e.target.value }
                            })}
                            className="px-2 py-1 rounded border"
                          >
                            <option value="12">12px</option>
                            <option value="16">16px</option>
                            <option value="20">20px</option>
                            <option value="24">24px</option>
                            <option value="32">32px</option>
                          </select>
                        </div>
                        <textarea
                          value={item.value}
                          onChange={(e) => updateContentItem(index, { value: e.target.value })}
                          className="w-full p-2 border rounded min-h-[100px]"
                          placeholder="Enter text..."
                          style={item.style}
                        />
                      </div>
                    ) : (
                      <div className="space-y-2">
                        <input
                          type="file"
                          accept={item.type === 'image' ? 'image/*' : item.type === 'video' ? 'video/*' : 'audio/*'}
                          onChange={(e) => handleFileUpload(e, index)}
                          className="block w-full text-sm"
                        />
                        {item.url && (
                          <>
                            <div className="flex items-center space-x-2">
                              <label className="text-sm">Size:</label>
                              <input
                                type="range"
                                min="20"
                                max="100"
                                value={item.size || 100}
                                onChange={(e) => updateContentItem(index, { size: parseInt(e.target.value) })}
                                className="flex-1"
                              />
                              <span className="text-sm">{item.size || 100}%</span>
                            </div>
                            {item.type === 'image' && (
                              <img src={item.url} alt="Content" style={{ width: `${item.size}%` }} className="rounded" />
                            )}
                            {item.type === 'video' && (
                              <video src={item.url} controls style={{ width: `${item.size}%` }} className="rounded" />
                            )}
                            {item.type === 'audio' && (
                              <audio src={item.url} controls className="w-full" />
                            )}
                          </>
                        )}
                      </div>
                    )}
                  </div>
                ))}

                {content.length === 0 && (
                  <p className="text-gray-400 text-center py-8">No content yet. Add text or media using the toolbar above.</p>
                )}
              </div>
            ) : (
              <div id="content-display" className="space-y-4">
                {content.map((item, index) => (
                  <div key={index}>
                    {item.type === 'text' && (
                      <p style={item.style} className="text-lg">{item.value}</p>
                    )}
                    {item.type === 'image' && item.url && (
                      <img src={item.url} alt="Question content" style={{ width: `${item.size}%` }} className="rounded" />
                    )}
                    {item.type === 'video' && item.url && (
                      <video src={item.url} controls style={{ width: `${item.size}%` }} className="rounded" />
                    )}
                    {item.type === 'audio' && item.url && (
                      <audio src={item.url} controls className="w-full" />
                    )}
                  </div>
                ))}
                {content.length === 0 && (
                  <p className="text-gray-400 text-center py-8">No content available.</p>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  // Main Render
  return (
    <div id="quiz-app">
      {currentScreen === 'home' && <HomeScreen />}
      {currentScreen === 'main' && <QuizGrid isAdmin={false} />}
      {currentScreen === 'admin' && <QuizGrid isAdmin={true} />}
    </div>
  );
};

export default QuizApp;
